using System;
using System.Text;

namespace Lzo64
{
    class Program
    {
        private readonly LZOCompressor compressor = new LZOCompressor();

        static void Main(string[] args)
        {
            try
            {
                var program = new Program();
                program.TestCompressor();
            }
            catch (Exception e)
            {
                Console.WriteLine(e);
            }
            Console.ReadLine();
        }

        public void TestCompressor()
        {
            Console.WriteLine("version=" + compressor.Version);

            var bytes = Encoding.UTF8.GetBytes(RandomString());
            var compressed = compressor.Compress(bytes, true);
            Console.WriteLine("uncompressed {0:n0} compressed {1:n0}", bytes.Length, compressed.Length);
            var decompressed = compressor.Decompress(compressed);
            //bytes[512] = 1;
            for (int i = 0; i < bytes.Length; i++)
            {
                if (bytes[i] != decompressed[i])
                    throw new Exception(string.Format("byte at position {0} does not match", i));
            }

            Console.WriteLine("matches");
        }

